import { useEffect, useState } from 'react';
import {Container, Row, Col } from 'react-bootstrap';
//import { useLocation } from 'react-router-dom';
import axios from 'axios';
import {CardComponent} from '../Components/CardComponent';
function ThirdPage(){
        console.log("Third Page");
        //const location = useLocation();
        //const email = location.state.email;
        //const password = location.state.password;
        //console.log(email);
        //console.log(password);
        const getUrl = 'https://tutorial4-api.herokuapp.com/api/users/';
        const [records, setRecords] = useState([]); 

        useEffect( () =>{
            async function fetchData(){
                await axios.get(getUrl)
                .then(response => {
                    //console.log(JSON.stringify(response));
                    setRecords(response.data.data);

                })
                .catch(error => {
                    //console.log(error);
                });

                // console.log("Records");
                //console.log(records);
            }
            fetchData();
        }, [records]);

        const sortArray = (Type) =>{
            const Types = {
                NoFilter: "id",
                FirstName: "firstName",
                LastName: "lastName" 
            };

            const sortProperty = Types[Type];
            const sorted = records.sort((a,b) => b)


        }


    return (<div>
        <Container fluid='md' className = 'm-5' padding = "10">
            <Row>
                <Col>
                <select onChange={(e) => sortArray(e.target.value)}>
                    <option value="NoFilter">Select Sortby option:</option>
                    <option value="FirstName">First Name</option>
                    <option value="LastName">Last Name</option>
                </select>
                </Col>
            </Row>
            {records.map((record) => {
                return (
                <Row>
                    <Col>
                        <CardComponent ID = {record.id} title = {record.title} firstName = {record.firstName} lastName = {record.lastName} picture = {record.picture} email = {record.email} />
                    </Col>
                </Row>
                );
            })}
        </Container>
    </div>);
}

export default ThirdPage;
