import {Card, Row, Col } from 'react-bootstrap';
//import SecondPage from './SecondPage';


export const CardComponent = (Props) => {
    return (

            <Card style = {{color: '#000'}} onClick = {() => {
                alert(Props.ID); 
            }}>
                <Row>
                    <Col>
                        <Card.Img variant="Left" src = {Props.picture} wid/>
                    </Col>
                    <Col>
                    <Row>
                        <Card.Title>
                            Welcome {Props.title}. {Props.firstName}
                        </Card.Title>
                    </Row>
                    <Row>
                        <Card.Body>
                            <Row>
                                Your ID : {Props.ID}<br/>
                            </Row>
                            <Row>
                                FirstName : {Props.firstName}<br/>
                            </Row>
                            <Row>
                                LastName : {Props.lastName}<br/>
                            </Row>
                            <Row>
                                Email : {Props.email}<br/>
                            </Row>
                        </Card.Body>
                    </Row>
                    </Col>
                </Row>

            </Card>

    );
}

export default CardComponent;
