import {Card, Row, Col } from 'react-bootstrap';
//import SecondPage from './SecondPage';
import { useHistory } from 'react-router-dom'; 


export const CardComponent = (Props) => {
    const history  = useHistory();
    const data = {
        "email": Props.email,
        "ID": Props.id,
        "title": Props.title,
        "firstName": Props.firstName,
        "lastName": Props.lastName,
        "picture": Props.picture
    }

    const handleClick = () =>{
        //alert("Clicked");
        console.log("Clicked Card");
        history.
        history.push({pathName:"FourthPage", state: {data: {data}}});
    }

    return (

            <Card style = {{color: '#000'}} onClick = {() => {
                handleClick()
            }}>
                <Row>
                    <Col>
                        <Card.Img variant="Left" src = {Props.picture} width = "100%"/>
                    </Col>
                    <Col>
                    <Row>
                        <Card.Title>
                            Welcome {Props.title}. {Props.firstName}
                        </Card.Title>
                    </Row>
                    <Row>
                        <Card.Body>
                            <Row>
                                Your ID : {Props.ID}<br/>
                            </Row>
                            <Row>
                                FirstName : {Props.firstName}<br/>
                            </Row>
                            <Row>
                                LastName : {Props.lastName}<br/>
                            </Row>
                            <Row>
                                Email : {Props.email}<br/>
                            </Row>
                        </Card.Body>
                    </Row>
                    </Col>
                </Row>

            </Card>

    );
}

export default CardComponent;
